package ProgramsByMe;

public class StringMethods20224 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String x="Hello Everyone!";
String y="Myself Jaya";
System.out.println(x.charAt(4));
System.out.println(y.charAt(6));
System.out.println(x.compareTo(y));
System.out.println(x.compareToIgnoreCase(y));
System.out.println(x.concat(y));
System.out.println(x.contains("Hello"));
System.out.println(x.contains(x));
System.out.println(x.endsWith("!"));
System.out.println(x.equals(y));
System.out.println(y.equalsIgnoreCase(y));
System.out.println();
System.out.println(x.indexOf("E"));
System.out.println(y.indexOf("Jaya"));
System.out.println(x.indexOf("Every"));
System.out.println(y.indexOf("E"));


	}

}
